def priority(current_city: int, distances: np.ndarray, unvisited: set[int]) -> np.ndarray:
    """定义启发式策略，为 TSP 选择下一座城市"""
    priorities = np.zeros(len(unvisited))
    for idx, city in enumerate(unvisited):
        if distances[current_city, city] > 0:
            priorities[idx] = 1 / distances[current_city, city]
        else:
            priorities[idx] = float('inf')

    for idx, city in enumerate(unvisited):
        if city % 2 == 0:
            priorities[idx] *= 0.9
        else:
            priorities[idx] *= 1.1

    return priorities
