def priority(current_city: int, distances: np.ndarray, unvisited: set[int]) -> np.ndarray:
    """定义启发式策略，为 TSP 选择下一座城市"""
    priorities = np.zeros(len(unvisited))
    for i, city in enumerate(unvisited):
        if distances[current_city, city] > 0:
            priority = 1 / distances[current_city, city]
        else:
            priority = float('inf')

        if priority < 0.5:
            priorities[i] = priority * 2
        elif priority >= 0.5 and priority < 1:
            priorities[i] = priority * 1.5
        else:
            priorities[i] = priority

    return priorities
