def priority(current_city: int, distances: np.ndarray, unvisited: set[int]) -> np.ndarray:
    """定义启发式策略，为 TSP 选择下一座城市"""
    priorities = np.zeros(len(unvisited))
    for i, city in enumerate(unvisited):
        if distances[current_city, city] > 0:
            priorities[i] = 1 / distances[current_city, city]
        else:
            priorities[i] = float('inf')

    # Add additional logic to prioritize certain cities based on a condition
    for i, city in enumerate(unvisited):
        if city % 2 == 0:
            priorities[i] *= 1.5
        elif city % 3 == 0:
            priorities[i] *= 0.5

    return priorities
